let swiper=new Swiper(".product-slider",{spaceBetween:0,effect:"fade",autoplay:{delay:5e3,disableOnInteraction:!1},centeredSlides:!0});function addActionsForm(){const e=document.querySelector(".form");openForm(e),closeForm(e)}function openForm(e){document.querySelector(".btn-block").addEventListener("click",t=>{t.preventDefault(),(t.target.dataset.mask||t.target.dataset.antiseptic)&&e.classList.add("active")})}function closeForm(e){const t=document.querySelector(".form__close"),o=document.querySelector(".form__container");e.addEventListener("click",r=>{r.target!==o&&r.target!==t||e.classList.remove("active")})}function validation(){const e=document.querySelector(".form__btn"),t=document.querySelector(".form"),o=document.querySelector('input[type="tel"]'),r=document.querySelector('input[type="text"]');e.addEventListener("click",e=>{if(e.preventDefault(),validationField(o)&&validationField(r))return t.classList.remove("active"),void alert("Заявка принята");alert("Введите корректные значения")})}function validationField(e){return""===e.value.trim()?(e.style.borderBottom="1px solid red",!1):(e.style.borderBottom="1px solid green",!0)}addActionsForm(),validation();